public class ForTest_04 {
	// 嵌套for循环

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <= 10; i++) {
			for (int j = 0; j < 3; j++) {

				System.out.println("j=" + j);

			}
			System.out.println("=================");

			System.out.println("i=" + i);

		}

	}

}
